<script>
import { getAuth, signOut, onAuthStateChanged } from 'https://www.gstatic.com/firebasejs/9.7.0/firebase-auth.js'


import { 
    getFirestore, 
    collection, 
    doc, 
    getDocs, 
    addDoc, 
    updateDoc, 
    deleteDoc, 
    query,
    where,
    onSnapshot } from 'https://www.gstatic.com/firebasejs/9.7.0/firebase-firestore.js'

import { 
    getStorage, 
    ref, 
    getDownloadURL } from 'https://www.gstatic.com/firebasejs/9.7.0/firebase-storage.js'
    
import { emitter } from '../main.js'

export default {  
  name:'ListeView',
  data() {
    return{                
        user:{
            email:null,
            password:null
        },
        message:null,
        avatar:null,
        userName:null,
        isAdmin:null,
        ville:null,
        niveau:null
    }
  },

  mounted(){
          let user = getAuth().currentUser;
          if(user){
              this.getUserInfo(user);
              this.user = user;
              console.log('user: ' + this.user.email);
              this.message = "User déjà connecté : "+this.user.email;
          }else{
              this.message = "";
              this.$router.push("/");
          }
  },
  
  methods:{
      async getUserConect(){
        //await
      },

      onCnx(){
          signInWithEmailAndPassword(getAuth(), this.user.email, this.user.password)
          .then((response)=>{
              console.log('user connecté', response.user);
              this.user = response.user;
              console.log('User connecté :', this.user.email)
              this.message = "User connecté : "+this.user.email;
          })
          .catch((error) =>{
              console.log('Erreur connexion', error);
              this.message = "Erreur d'authentification";
          })
      },
      onDcnx(){
          signOut(getAuth())
          .then(response =>{
              this.user = getAuth().currentUser;
              this.user = {
                  email:null,
                  password:null,
                  displayName:null
              };
              emitter.emit('deConnectedUser', { user : this.user });
              console.log("user deconnecté ", this.user);        
              this.message = 'user non connecté';
              this.$router.push("/");
          })
          .catch(error=>{
              console.log('erreur deconnexion ', error);
          })

      },
      async getUserInfo(user){
        const fireStore = getFirestore();
        const dbUser = collection(fireStore, "compte")
        const q = query(dbUser, where("uid", "==", user.uid));
        await onSnapshot(q, (snapshot) => {
          this.userInfo = snapshot.docs.map(doc => (
            {id:doc.id, ...doc.data()}
          ));
          console.log("userInfo", this.userInfo)
          
          this.userName = this.userInfo[0].userName;
          this.isAdmin = this.userInfo[0].isAdmin;
          this.ville = this.userInfo[0].ville;
          this.niveau = this.userInfo[0].niveau;

          const spaceRef = ref(getStorage(), 'user/'+this.userInfo[0].avatar);
          getDownloadURL(spaceRef)
            .then((url) => {
              this.avatar = url;
            })
            .catch((error) =>{
              console.log('erreur downloadURL', error)
            })
        })
      }
  }
}
</script>



